//This is for ai profiles generated by AI
const ProductFaqs = (sequelize, Sequelize) => {
  const Values = sequelize.define("ProductFaqs", {
    question: {
      type: Sequelize.TEXT,
      defaultValue: "",
    },
    answer: {
      type: Sequelize.TEXT,
      defaultValue: "",
    },
    type: {
      type: Sequelize.TEXT,
      defaultValue: "ai", // "ai", "manual"
    },
    kbType: {
      type: Sequelize.TEXT,
      defaultValue: "", //video, kb
    },
    kbId: {
      type: Sequelize.INTEGER,
      allowNull: true,
    },
    userId: {
      type: Sequelize.INTEGER,
      allowNull: true,
      references: {
        model: "Users", // Table name (plural form)
        key: "id",
      },
    },
  });

  return Values;
};

export default ProductFaqs;
