//This is for ai profiles generated by AI
const CommunicationInstructions = (sequelize, Sequelize) => {
  const Values = sequelize.define("CommunicationInstructions", {
    title: {
      type: Sequelize.TEXT,
      defaultValue: "", //optional
    },
    pacing: {
      type: Sequelize.TEXT,
      defaultValue: "", //optional
    },
    tone: {
      type: Sequelize.TEXT,
      defaultValue: "", //optional
    },
    intonation: {
      type: Sequelize.TEXT,
      defaultValue: "", //optional
    },
    scenario: {
      type: Sequelize.TEXT,
      defaultValue: "", //optional
    },
    prompt: {
      type: Sequelize.TEXT,
      defaultValue: "", //optional
    },
    response: {
      type: Sequelize.TEXT,
      defaultValue: "", //optional
    },
    description: {
      type: Sequelize.TEXT,
      defaultValue: "",
    },
    type: {
      type: Sequelize.TEXT,
      defaultValue: "ai", // "ai", "manual"
    },
    kbType: {
      type: Sequelize.TEXT,
      defaultValue: "", //video, kb
    },
    kbId: {
      type: Sequelize.INTEGER,
      allowNull: true,
    },
    userId: {
      type: Sequelize.INTEGER,
      allowNull: true,
      references: {
        model: "Users", // Table name (plural form)
        key: "id",
      },
    },
  });

  return Values;
};

export default CommunicationInstructions;
